import React from "react";
import {Text,TextInput, StyleSheet, View, ImageBackground } from "react-native";

export default function RegistrationScreen()  {
return (
  <View style={styles.container}>
<ImageBackground source={require('./assets/photo_bg.jpg')}  style={styles.img}>

  <View style={styles.test}>
  <View style={styles.inputWrapper}>
    <View style={styles.titlePadding}>
    <Text  style={styles.title} >Register</Text>
    </View>

<TextInput
style={styles.input}
placeholder='Type login here'
/>
<TextInput
style={styles.input}
placeholder='Type email here'
/>
<TextInput
style={styles.input}
placeholder='Type password here'
/>
</View>
</View>
</ImageBackground>
  </View>
)
};

const styles = StyleSheet.create ({
  container:{
flex:1,
backgroundColor:'#000000'
  },
  img:{
    flex:1,
   resizeMode:'cover',
   justifyContent: 'center'
  },
  test:{
    backgroundColor:'#FFFFFF',
    height:600,
    width:410,
    borderRadius:25,
   position:'absolute',
   top:300,
  },
  titlePadding:{
    paddingTop:20,
  },
  title:{
    color:'#212121',
    width: 184,
    height: 35,
    fontWeight:500,
    fontSize:30,
    marginBottom:33,
    textAlign: 'center',

  },
  inputWrapper:{
flex:1,
alignItems: 'center',
  },

  input:{
    width:343,
    height: 50,
    backgroundColor:'#E8E8E8',
    borderRadius:8,
    padding:16,
    marginBottom:16
  },

})
