import React from "react";
import { useState } from "react";
import { StatusBar } from 'expo-status-bar';
import { Button, StyleSheet, Text, View, TouchableWithoutFeedback, KeyboardAvoidingView, Platform, TouchableOpacity, Keyboard} from 'react-native';

import { TextInput } from "react-native";

 function  RegistrationScreen () {

    const initialState ={
        email: '',
        password: ''
    }
const keyboardHide = () =>{
  isShowKeyboard(false);
  Keyboard.dismiss()
}
  // const [email, setEmail] = useState("");
  // const [password, setPassword] = useState("");
  // const [login, setLogin] = useState("");
  const [isShowKeyboard, setIsShowKeyboard ] = useState(false)
const [state, setState] = useState(initialState)

//   const loginHandler = (login) => setLogin(login);
//   const emailHandler = (email) => setEmail(email);
//   const passwordHandler = (password )=> setPassword(password);
  return (
   <View>
    <TouchableWithoutFeedback onPress={()=> Keyboard.dismiss()}>
    <KeyboardAvoidingView
      behavior={Platform.OS === 'ios' ? 'padding' : ''}
      > 
    {/* додати styles.form : margin bottom : -+ 100 */}
  
      <View>
    <Text  style={styles.titleText} >Register Form</Text>
    <TextInput
      style = {styles.input} textAlign={'center'} 
      placeholder='Type login here'
      onFocus={()=>setIsShowKeyboard(true)}
    //   value={login}
      onChangeText={(value) => setState((prevState) => ({...prevState, login: value}))}
      />
      <TextInput
      style = {styles.input} textAlign={'center'} 
      placeholder='Type email here'
      onFocus={()=>setIsShowKeyboard(true)}
    //   value={email}
      onChangeText={(value) => setState((prevState) => ({...prevState, email: value}))}
      />
       <TextInput
       secureTextEntry ={true}
         style = {styles.input} textAlign={'center'} 
      placeholder='Type password here'
      onFocus={()=>setIsShowKeyboard(true)}
    //   value={password}
    onChangeText={(value) => setState((prevState) => ({...prevState, password: value}))}
      />
      <TouchableOpacity onPress={keyboardHide}>
        <Text >Register</Text>
      </TouchableOpacity>
      </View>
      </KeyboardAvoidingView>
      </TouchableWithoutFeedback>
   </View>
  );
};

const styles = StyleSheet.create({
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
    textAlign: 'center'
  },
  titleText: {
    fontSize: 20,
    fontWeight: 'bold',
    textAlign:'center'

  },
});

export default RegistrationScreen;
