import React from "react";
import { useState } from "react";
import { StatusBar } from 'expo-status-bar';
import { Button, StyleSheet, Text, View, TouchableWithoutFeedback  } from 'react-native';

import { TextInput } from "react-native";

 function  RegistrationScreen () {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [login, setLogin] = useState("");
const [state, setState] = useState(initialState)

  const loginHandler = (login) => setLogin(login);
  const emailHandler = (email) => setEmail(email);
  const passwordHandler = (password )=> setPassword(password);
  return (
   <View>
    <Text  style={styles.titleText} >Register Form</Text>
    <TextInput
      style = {styles.input} textAlign={'center'} 
      placeholder='Type login here'
      value={login}
      onChangeText={loginHandler}
      />
      <TextInput
      style = {styles.input} textAlign={'center'} 
      placeholder='Type email here'
      value={email}
      onChangeText={emailHandler}
      />
       <TextInput
       secureTextEntry ={true}
         style = {styles.input} textAlign={'center'} 
      placeholder='Type password here'
      value={password}
      onChangeText= {passwordHandler}
      />
      
   
   </View>
  );
};

const styles = StyleSheet.create({
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
    textAlign: 'center'
  },
  titleText: {
    fontSize: 20,
    fontWeight: 'bold',
    textAlign:'center'

  },
});

export default RegistrationScreen;
